// Utility function to convert Base64 to Uint8Array
function base64ToUint8Array(base64: string): Uint8Array {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/';
  let binaryString = '';

  // Decode Base64
  for (let i = 0; i < base64.length; i += 4) {
    const encoded1 = chars.indexOf(base64[i]);
    const encoded2 = chars.indexOf(base64[i + 1]);
    const encoded3 = chars.indexOf(base64[i + 2]);
    const encoded4 = chars.indexOf(base64[i + 3]);

    const byte1 = (encoded1 << 2) | (encoded2 >> 4);
    const byte2 = ((encoded2 & 15) << 4) | (encoded3 >> 2);
    const byte3 = ((encoded3 & 3) << 6) | encoded4;

    binaryString += String.fromCharCode(byte1);
    if (encoded3 !== 64) binaryString += String.fromCharCode(byte2);
    if (encoded4 !== 64) binaryString += String.fromCharCode(byte3);
  }

  // Convert binary string to Uint8Array
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes;
}

// Function to add image fill to frames with opacity
async function addImageFillToFrames(frameNodes: readonly SceneNode[], opacity: number) {
  for (const frameNode of frameNodes) {
    if (frameNode.type === "FRAME") {
      try {
        // Example Base64-encoded image data (replace with your own)
        const imageUrl = "data:image/png;base64,"; // Your Base64 image data here
        const imageData = imageUrl.split(',')[1]; // Get the Base64 part

        // Convert Base64 to Uint8Array
        const bytes = base64ToUint8Array(imageData);

        // Create the image in Figma and get the image hash
        const image = figma.createImage(bytes);
        const imageHash = image.hash;
        console.log("Image created successfully. Hash:", imageHash);

        // Create a rectangle that covers the entire frame
        const backgroundRect = figma.createRectangle();
        backgroundRect.resize(frameNode.width, frameNode.height);
        backgroundRect.fills = [{
          type: "IMAGE",
          scaleMode: "FILL",
          imageHash: imageHash,
          opacity: opacity / 100 // Convert to a value between 0 and 1
        }];

        // Move the rectangle behind the other elements
        frameNode.appendChild(backgroundRect);
        //frameNode.insertChild(0, backgroundRect); // Insert rectangle at the bottom

        figma.notify(`Image fill added to frame: "${frameNode.name}" with opacity: ${opacity}%`);
        console.log(`Image fill applied to frame "${frameNode.name}" with opacity:`, opacity);

      } catch (error) {
        console.error("Error creating image fill for frame:", frameNode.name, error);
        figma.notify(`Failed to create image fill for "${frameNode.name}": ` );
      }
    } else {
      figma.notify(`Selected node "${frameNode.name}" is not a frame.`);
    }
  }
}

// Listen for messages from the UI
figma.ui.onmessage = (msg) => {
  if (msg.type === 'set-opacity') {
    const selectedNodes = figma.currentPage.selection; // Get all selected nodes
    if (selectedNodes.length > 0) {
      addImageFillToFrames(selectedNodes, msg.opacity).catch(error => {
        console.error("Unexpected error:", error);
        figma.notify("Unexpected error. Check console for details.");
      });
    } else {
      figma.notify("No frames selected.");
    }
  }
};

